sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var gr = new GlideRecord('incident');
    gr.addQuery('active', true);
    gr.addQuery('assignment_group', current.getValue('assignment_group'));
    gr.query();
    var num = gr.getRowCount();

    template.print('<a href="https://dev109596.service-now.com/incident_list.do?sysparm_query=active%3Dtrue%5Eassignment_group%3D' + current.getValue('assignment_group') + '&sysparm_view=">' + num + '</a>');



})(current, template, email, email_action, event);
